import axios from 'axios';

// Для production - относительные пути
const API_BASE_URL = '/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Profiles - ОДНА функция getProfiles
export const getProfiles = async (filters = {}) => {
  const response = await api.get('/profiles/', { 
    params: {
      page: filters.page || 1,
      page_size: filters.page_size || 12,
      game: filters.game,
      sort_by: filters.sortBy,
      search: filters.search,
      rank: filters.rank,
      has_achievements: filters.achievements
    }
  });
  return response.data;
};

export const getProfile = async (id) => {
  const response = await api.get(`/profiles/${id}/`);
  return response.data;
};

// Games
export const getGames = async () => {
  const response = await api.get('/games/');
  return response.data;
};

// Posts
export const getPosts = async (userId) => {
  const response = await api.get('/posts/', { 
    params: { author: userId } 
  });
  return response.data;
};

export const createPost = async (postData) => {
  const response = await api.post('/posts/', postData);
  return response.data;
};

export const updatePost = async (id, postData) => {
  const response = await api.put(`/posts/${id}/`, postData);
  return response.data;
};

export const deletePost = async (id) => {
  const response = await api.delete(`/posts/${id}/`);
  return response.data;
};

// Subscriptions
export const getSubscriptionPlans = async (userId) => {
  const response = await api.get('/subscription-plans/', {
    params: { author: userId }
  });
  return response.data;
};

// User Games
export const getUserGames = async (userId) => {
  const response = await api.get('/user-games/', {
    params: { user: userId }
  });
  return response.data;
};

export default api;
